import React, {useState, useEffect} from "react";
import {cities} from "../constants";
import styles from "./NavBar.module.css"

function NavBar(){
    let initialCities=[];
    for(let i=0;i<5;i++){
        initialCities[i] = cities[i]
      }
    const [customCities, setCutomCities] = useState(initialCities)
    const size = cities.length;
   
    //console.log(customCities)
   
   
   const nextHandler =(lastIndex)=>{
    console.log("PREVINDEX:: ",initialCities[0]);
    const index= cities.findIndex((name)=>name === initialCities[0]);
    console.log("CURRENTINDEX:: ",index);
    initialCities=[];
    let tempIndex=0;
    for(let i=index+1;i<lastIndex+1;i++){
        initialCities[tempIndex] = cities[i];
     }
     console.log("CURRENT CITIES:: ",initialCities);
     setCutomCities(initialCities);
   }
//    useEffect(()=>{
//       setCutomCities(initialCities);
//    },[])

    return (
        <div className={styles["city-container"]}>
         <button key={"prev"}>{"<<"}</button>
         { customCities && customCities.map((_,index)=>(
    
                <button key={`city${index}`}>{cities[index]}</button>
         ))}
          <button name="spread" key="spread">...</button>
         <button key="size">{size}</button>
         <button onClick={()=>nextHandler(customCities.length)} key="next">{">>"}</button>
        </div>
    );

}
export default NavBar;
