    const longitude = position.coords.longitude;
    try{
        const reponse =await  fetch(`https://api.weatherapi.com/v1/current.json?key=dc27acb7394c462a8a6133905240105&q=${latitude},${longitude}`);

       if(reponse.ok){
        const data = await reponse.data;
