import React, {useState, useEffect} from "react";
import {cities} from "../constants";
import styles from "./NavBar.module.css"

let start =[];
for(let i= 0;i<5;i++){
    //console.log("PUSHED", cities[i])
   start.push(cities[i]);
 }

function NavBar(){
    const [customCities, setCutomCities] = useState([...start]);
    //const [initialCities, setInitialStates] = useState(start);
    //console.log("CURRENT CITIES:: ",customCities[0]);
    const size = cities.length;
    

    //console.log(customCities)
   
   
   const nextHandler =()=>{
    console.log("CURRENT CITIES:: ",customCities);
    const firstElement =  cities.findIndex((name)=>name === customCities[0]);
    const lastElement =  cities.findIndex((name)=>name === customCities[customCities.length-1]);
    //console.log("INDEX:: ",firstElement, lastElement);
    let newList =[]
    for(let i= firstElement+1;i<=lastElement+1;i++){
        //console.log("PUSHED", cities[i])
       newList.push(cities[i])
     }
     setCutomCities(newList);
   }
   useEffect(() => {
    console.log("Custom cities updated:", customCities);
  }, [customCities]);

    return (
        <div className={styles["city-container"]}>
         <button key={"prev"}>{"<<"}</button>
         { customCities && customCities.map((_,index)=>(
    
                <button key={`city${index}`}>{cities[index]}</button>
         ))}
          <button name="spread" key="spread">...</button>
         <button key="size">{size}</button>
         <button onClick={nextHandler} key="next">{">>"}</button>
        </div>
    );

}
export default NavBar;
